#!/bin/bash
gnome-terminal --tab --title="Logger" --command="bash -c 'java airportrhapsody.serverSide.Logger.ServerLogger ; $SHELL'"  
gnome-terminal --tab --title="Arrival Lounge" --command="bash -c 'java airportrhapsody.serverSide.ArrivalLounge.ServerArrivalLounge ; $SHELL'" --tab --title="Arr Term Exit" --command="bash -c 'java airportrhapsody.serverSide.ArrTermExit.ServerArrTermExit ; $SHELL'" --tab --title="Arr Trans Quay" --command="bash -c 'java airportrhapsody.serverSide.ArrTransQuay.ServerArrTransQuay ; $SHELL'" --tab --title="Coll Point" --command="bash -c 'java airportrhapsody.serverSide.CollectionPoint.ServerCollectionPoint ; $SHELL'" --tab --title="Dep Term Entrance" --command="bash -c 'java airportrhapsody.serverSide.DepTermEntrance.ServerDepTermEntrance ; $SHELL'" --tab --title="DepTransQuay" --command="bash -c 'java airportrhapsody.serverSide.DepTransQuay.ServerDepTransQuay ; $SHELL'" --tab --title="ReclaimOffice" --command="bash -c 'java airportrhapsody.serverSide.ReclaimOffice.ServerReclaimOffice ; $SHELL'" --tab --title="TempStorageArea" --command="bash -c 'java airportrhapsody.serverSide.TempStorageArea.ServerTempStorageArea ; $SHELL'" 
gnome-terminal --tab --title="Porter" --command="bash -c 'java airportrhapsody.clientSide.PorterMain ; $SHELL'" --tab --title="BusDriver" --command="bash -c 'java airportrhapsody.clientSide.BusDriverMain ; $SHELL'" --tab --title="Passenger 0" --command="bash -c 'java airportrhapsody.clientSide.PassengerMain 0 ; $SHELL'" --tab --title="Passenger 1" --command="bash -c 'java airportrhapsody.clientSide.PassengerMain 1 ; $SHELL'" --tab --title="Passenger 2" --command="bash -c 'java airportrhapsody.clientSide.PassengerMain 2 ; $SHELL'" --tab --title="Passenger 3" --command="bash -c 'java airportrhapsody.clientSide.PassengerMain 3 ; $SHELL'" --tab --title="Passenger 4" --command="bash -c 'java airportrhapsody.clientSide.PassengerMain 4 ; $SHELL'" --tab --title="Passenger 5" --command="bash -c 'java airportrhapsody.clientSide.PassengerMain 5 ; $SHELL'"
